create table equipment (exercise_id bigint, id bigint generated by default as identity, condition varchar(255), name varchar(255), type varchar(255), primary key (id));
create table exercise (id bigint generated by default as identity, description varchar(255), name varchar(255), target_muscle varchar(255) check (target_muscle in ('CHEST','LEGS','TOP')), primary key (id));
create table gym (closing_hour float(53), opening_hour float(53), id bigint generated by default as identity, address varchar(255), name varchar(255), phone varchar(255), primary key (id));
create table gym_equipment (equipment_id bigint not null, gym_id bigint not null);
create table pass (cost float(53), end_date timestamp(6), id bigint generated by default as identity, start_date timestamp(6), pass_type varchar(255) check (pass_type in ('MONTHLY','ANNUAL','DAILY')), primary key (id));
create table user_exercise (exercise_id bigint not null, user_id bigint not null);
create table user_details (active boolean not null, id bigint generated by default as identity, pass_id bigint unique, email varchar(255), name varchar(255), phone varchar(255), user_role varchar(255) check (user_role in ('USER','EMPLOYEE','ADMINISTRATOR')), primary key (id));
alter table if exists equipment add constraint FKd58fdrndxbdfxdbhya451j8rv foreign key (exercise_id) references exercise;
alter table if exists gym_equipment add constraint FKb13axkyadtyffci5t9dxjaa23 foreign key (equipment_id) references equipment;
alter table if exists gym_equipment add constraint FKf9hi0fo971xm30ydv24lcd181 foreign key (gym_id) references gym;
alter table if exists user_exercise add constraint FK4dsfvd3ee924pwq4078equ1tu foreign key (exercise_id) references exercise;
alter table if exists user_exercise add constraint FKn1dbwyk7c0cjnk34ykfvxn355 foreign key (user_id) references user_details;
alter table if exists user_details add constraint FKmu3qqjuftewuov0m5mwe6wgid foreign key (pass_id) references pass;
